CREATE TABLE blog_article (id BIGINT AUTO_INCREMENT, title VARCHAR(255), content LONGTEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE blog_comment (id BIGINT AUTO_INCREMENT, blog_article_id BIGINT, author VARCHAR(255), content LONGTEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX blog_article_id_idx (blog_article_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE login (id INT AUTO_INCREMENT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, first_name VARCHAR(50) NOT NULL, last_name VARCHAR(50) NOT NULL, email VARCHAR(100) NOT NULL, username VARCHAR(50) NOT NULL, password VARCHAR(64) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE nomination (id_nomination INT AUTO_INCREMENT, id_company INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, id_nominator INT NOT NULL, first_name VARCHAR(40) NOT NULL, last_name VARCHAR(40) NOT NULL, username VARCHAR(30) NOT NULL, password VARCHAR(64) NOT NULL, salt VARCHAR(64) NOT NULL, email VARCHAR(70) NOT NULL, designation VARCHAR(50) NOT NULL, address VARCHAR(255) NOT NULL, country VARCHAR(40) NOT NULL, state VARCHAR(40) NOT NULL, city VARCHAR(40) NOT NULL, age INT NOT NULL, contact_no VARCHAR(20) NOT NULL, hobby VARCHAR(50) NOT NULL, interest VARCHAR(50) NOT NULL, education VARCHAR(255) NOT NULL, work_experience VARCHAR(255) NOT NULL, user_image VARCHAR(150) NOT NULL, nomination_date DATETIME NOT NULL, user_click INT NOT NULL, is_nominator VARCHAR(1) NOT NULL, linked_with VARCHAR(70) NOT NULL, approval_status INT NOT NULL, last_login DATETIME NOT NULL, PRIMARY KEY(id_nomination)) ENGINE = INNODB;
ALTER TABLE blog_comment ADD CONSTRAINT blog_comment_blog_article_id_blog_article_id FOREIGN KEY (blog_article_id) REFERENCES blog_article(id) ON DELETE CASCADE;
